package basicOpenLabBook;

public class SwapTwoNumbersExercise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i =10;
		int j = 20;
		System.out.println("Before swap i= "+ i + "and j =" +j);
		int temp =i;
		 i=j;
		 j=temp;
		 System.out.println("After swap i= "+ i + "and j =" +j);
	}

}
